import a from '../Admin/Admin.module.scss'
import React, { CSSProperties, useState } from 'react';
import Select from 'react-select';

const options = [
    { value: 'admin', label: 'Админ' },
    { value: 'advmoderator', label: 'Модератор рекламных кампаний' },
    { value: 'sitemoderator', label: 'Модератор рекламных площадок' },
    { value: 'lotmoderator', label: 'Модератор лотов аукциона' },
    { value: 'supermanager', label: 'Суперменеджер' },
    { value: 'manager', label: 'Менеджер' },
  ]

export default function Invitation () {

    const [InvitationValue, setInvitationValue] = useState('')

    const handleSelectChange = (selectedOption) => {
        setInvitationValue(selectedOption.value);
    };



    return (

        <>

        <p></p>

        <section className={a.section__home}>
            <div className="container">

                <p className={a.invitation__title}>Приглашение в админку</p>

                <form className={a.invitation}>

                    <input type="text" className={a.invitation__input} placeholder='Имя Фамилия'/>

                    <input type="text" className={a.invitation__input} placeholder='Почта'/>

                    <Select options={options} onChange={handleSelectChange} />

                    <button className={a.invitation__btn}>
                        Добавить 
                    </button>

                </form>

            </div>
        </section>

        </>

    )

}
