import Group from './img/Group.svg'


import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import TableItem from './TableItem';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../AuthContext';
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

// const labels = ['12.03.23', '12.03.30', '12.05.23', '12.06.23', '12.07.23', '12.08.23', '12.10.23'];


export default function Statisticss () {

  const apiUrl = process.env.REACT_APP_API_URL;

  const token = localStorage.getItem('access_token');

  const [VenuesData, setStaticData] = useState([])

  const { refreshToken } = useAuth();

  const [currentWeek, setCurrentWeek] = useState(true);

  const [startDate, setStartDate] = useState('2023-09-24'); 

  const [endDate, setEndDate] = useState('2023-09-27');

  const fetchData = () => {

    const loginEndpoint = `/api/statistic/by_date?start_date=${startDate}&end_date=${endDate}`;

    const url = `${apiUrl}${loginEndpoint}`;

    axios
      .get(url, {

        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },

      })

      .then(res => {

        setStaticData(res.data);

      })

      .catch(error => {

        if (error.response && error.response.status === 401) {

          refreshToken();

        } else {

        }
      });
  };


  const handleCurrentWeekClick =  async () => {

    const currentDate = new Date();

    setEndDate(currentDate.toISOString().split('T')[0]);

    currentDate.setDate(currentDate.getDate() - 7);

    setStartDate(currentDate.toISOString().split('T')[0]);

    await new Promise(resolve => setTimeout(resolve, 0));

    fetchData();


  };

  const handlePreviousWeekClick = () => {

    setCurrentWeek(false);


    const currentDate = new Date();
    const endDate = new Date(currentDate);
    const startDate = new Date(currentDate);

    startDate.setDate(currentDate.getDate() - 14);

    endDate.setDate(currentDate.getDate() - 7);

    const startDateString = startDate.toISOString().split('T')[0];
    const endDateString = endDate.toISOString().split('T')[0];

    setStartDate(startDateString);
    setEndDate(endDateString);

    fetchData();
  };

  const handleLast7DaysClick = () => {

    setCurrentWeek(false);

    const currentDate = new Date();
    const endDate = new Date(currentDate);
    const startDate = new Date(currentDate);

    startDate.setDate(currentDate.getDate() - 7);

    const startDateString = startDate.toISOString().split('T')[0];
    const endDateString = endDate.toISOString().split('T')[0];

    setStartDate(startDateString);
    setEndDate(endDateString);

    fetchData();
  };

  const handleLast30DaysClick = () => {

    setCurrentWeek(false);

    const currentDate = new Date();
    const endDate = new Date(currentDate);
    const startDate = new Date(currentDate);

    startDate.setDate(currentDate.getDate() - 30);

    const startDateString = startDate.toISOString().split('T')[0];
    const endDateString = endDate.toISOString().split('T')[0];

    setStartDate(startDateString);
    setEndDate(endDateString);

    fetchData();
  };

  const handleLast3MonthsClick = () => {
    setCurrentWeek(false);

    const currentDate = new Date();
    const endDate = new Date(currentDate);
    const startDate = new Date(currentDate);

    startDate.setMonth(currentDate.getMonth() - 3);

    const startDateString = startDate.toISOString().split('T')[0];
    const endDateString = endDate.toISOString().split('T')[0];

    setStartDate(startDateString);
    setEndDate(endDateString);

    fetchData();
  };

  const handleLastYearClick = () => {

    setCurrentWeek(false);

    const currentDate = new Date();
    const endDate = new Date(currentDate);
    const startDate = new Date(currentDate);

    startDate.setFullYear(currentDate.getFullYear() - 1);

    const startDateString = startDate.toISOString().split('T')[0];
    const endDateString = endDate.toISOString().split('T')[0];

    setStartDate(startDateString);
    setEndDate(endDateString);

    fetchData();
  };


