import React, { useEffect, useRef } from 'react';
import { useState } from 'react';
import s from './Statistics.module.scss'
import Select from 'react-select';
import { DatePicker } from 'antd';

import setting from './img/setting.svg'
import setbar from './img/setbar.svg'
import setting__title from './img/setting__title.svg'

import title_bar1 from './img/title_bar1.svg'
import title_bar2 from './img/title_bar2.svg'
import title_bar3 from './img/title_bar3.svg'
import array from './img/array.svg'

import Group from './img/Group.svg'


import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import TableItem from './TableItem';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../AuthContext';
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

// const labels = ['12.03.23', '12.03.30', '12.05.23', '12.06.23', '12.07.23', '12.08.23', '12.10.23'];


export default function Statisticss () {

  const apiUrl = process.env.REACT_APP_API_URL;

  const loginEndpoint = '/api/statistic/by_date?start_date=2023-09-24&end_date=2023-09-27';

  const token = localStorage.getItem('access_token');

  const [VenuesData, setStaticData] = useState([])

  const { refreshToken } = useAuth();

  useEffect(() => {

      const url = `${apiUrl}${loginEndpoint}`;

          axios.get(url, {

              headers: {
                  'Content-Type': 'application/json',
                  'Authorization': `Bearer ${token}` 
              }

          })
          .then(res => {

            setStaticData(res.data)

          })

          .catch(error => {

            if (error.response && error.response.status === 401) {

              refreshToken();

            } else {

