import { Link } from 'react-router-dom'
import r from './Register.module.scss'
import logo from '../img/logoReg.svg'
import ais from '../img/ice.svg'
import aisTwo from '../img/iceTwo.svg'
import { useState } from 'react';

export default function Register () {

    const [showPassword, setShowPassword] = useState(false);

    const [showPasswordTwo, setShowPasswordTwo] = useState(false);

    const togglePasswordVisibility = () => {

        setShowPassword(prevShowPassword => !prevShowPassword);

    };

    const [email, setEmail] = useState('');

    const [password, setPassword] = useState('');


    const handleEmailChange = (e) => {

        setEmail(e.target.value);

    };

    const handlePasswordChange = (e) => {

        setPassword(e.target.value);

    };

    const Login = (e) => {

        e.preventDefault()

        axios.post(`http://127.0.0.1:8000/api/auth/${emailRegex.test(ValuePocht) ? 'token-email' : 'token-phone'}/`,

        {
            email: emailRegex.test(ValuePocht) === true ? ValuePocht : null,
            phone: !emailRegex.test(ValuePocht) ? ValuePocht : null,
        },

        {
            headers : {

            'Content-Type': 'application/json',


            }

        }

        )

        .then(res => {
            res.request.status == 200 ? navigate('/') : navigate('/login')
            setIsActive(false)
            localStorage.setItem('isAuthorized', 'true');
            setToken(res.data.auth_token)
            localStorage.setItem('token', res.data.auth_token);
            window.location.reload()

        } )

        .catch(err => {

            err.message === 'Request failed with status code 400' ? setmodal(true) : setmodal(false)

        } )

    }




    return (

        <>

        <div className={r.section__register}>

                <div className={r.register}>

                    <img src={logo} className={r.register__logo} alt="svg" />

                    {/* <p className={r.register__title}>
                    Неверный Email или пароль
                    </p> */}

                    <form className={r.register__form}>

                        <label className={r.register__form__label}>

                            <p className={r.register__form__label__title}>
                            Email
                            </p>

                            <input type="text" autoComplete="new-password" className={r.register__form__label__input}
                            onChange={handleEmailChange}
                            />

                        </label>

                          <label className={r.register__form__label}>

                            <p className={r.register__form__label__title}>
                            Пароль
                            </p>

                            <div className={r.register__form__label__rel}>

                            <input

                             type={showPassword ? 'text' : 'password'} 
                             autoComplete="new-password"
                             className={r.register__form__label__input}
                             onChange={handlePasswordChange}

                             />

                             {showPassword ?

                             <img src={aisTwo} alt="" onClick={togglePasswordVisibility} />

                             :

                             <img src={ais} alt="" onClick={togglePasswordVisibility} />
                            }

                            </div>

                        </label>

                        <div className={r.register__form__footer}>

                        <label className={r.custom__checkbox}>

                            <input type="checkbox" />

                            <span className={r.checkmark}></span>

                            <p>Запомнить меня</p>

                        </label>

                            <Link to={'/restoring'} className={r.register__form__footer__title}>

                            Не помню пароль

                            </Link>

                        </div>

                        <button className={email.length > 0 && password.length > 0 ? r.register__form__codeBlue : r.register__form__code}>

                         Войти
                        </button>



                    </form>

                </div>

            </div>

        </>

    )
}
