import axios from 'axios';
import a from '../Admin/Admin.module.scss'
import React, { CSSProperties, useEffect, useState } from 'react';
import Select from 'react-select';

const options = [

    { value: 'admin', label: 'Админ' },
    { value: 'advmoderator', label: 'Модератор рекламных кампаний' },
    { value: 'sitemoderator', label: 'Модератор рекламных площадок' },
    { value: 'lotmoderator', label: 'Модератор лотов аукциона' },
    { value: 'supermanager', label: 'Суперменеджер' },
    { value: 'manager', label: 'Менеджер' },

  ]

export default function Invitation () {

    const [InvitationValue, setInvitationValue] = useState('')

    const handleSelectChange = (selectedOption) => {
        setInvitationValue(selectedOption.value);
    };


    const [userId, setUserId] = useState()

    const apiUrl = process.env.REACT_APP_API_URL;

    const loginEndpoint = '/api/rights/all';

    useEffect(() => {

        const token = localStorage.getItem('access_token');

        if (token) {

            const url = `${apiUrl}${loginEndpoint}`;

            axios.get(url, {

                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}` 
                }

            })
            .then(res => {

                setUserId(res.data)

            })

            .catch(error => {

            });
        }
    }, []);

    console.log(userId);




    return (

        <>


        <section className={a.section__home}>
            <div className="container">

                <p className={a.invitation__title}>Приглашение в админку</p>

                <form className={a.invitation}>

                    <input type="text" className={a.invitation__input} placeholder='Имя Фамилия'/>

                    <input type="text" className={a.invitation__input} placeholder='Почта'/>

                    <div className={a.in}></div>

                    <label className={a.invitation__label}>

                        <input type="checkbox" />

                        <div className={a.invitation__label__info}>

                            <p className={a.invitation__label__info__title}>jfouehvguow</p>

                            <p className={a.invitation__label__info__subtitle}>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, aperiam sed illum molestias modi reprehenderit neque velit consequuntur nam! Ipsam temporibus inventore incidunt quia quae voluptate, tenetur possimus. Illum, assumenda?</p>

                        </div>

                    </label>

                    <button className={a.invitation__btn}>
                        Добавить 
                    </button>

                </form>

            </div>
        </section>

        </>

    )

}
