import { BrowserRouter, Navigate, Route, Routes, useLocation } from 'react-router-dom';
import './style/App.css';

import ScrollTop from './utils/scrollTop';
import Header from './components/Header/Header';
import Register from './components/Auth/Register';
import Restoring from './components/Auth/Restoring';
import Shipped from './components/Auth/Shipped';
import NewPass from './components/Auth/NewPass';
import Admin from './pages/Admin/Admin';
import NotFound from './pages/notPages/NoPages';
import { useEffect } from 'react';

function App() {

  const hasAccessToken = !!localStorage.getItem('access_token');

  useEffect(() => {

    const hasAccessToken = !!localStorage.getItem('access_token');

  }, [])

  function PrivateRoute ({ element, authenticated, ...rest }) {

    return (

      <Route
        {...rest}
        element={authenticated ? element : <Navigate to="/" />}
      />

    );
  }

  return (

    <BrowserRouter>

    <div className="app">

      <ScrollTop />

      {hasAccessToken && <Header />}

        <Routes>

          <Route path='/'  element={<Register />} />

          <Route path='/restoring'  element={<Restoring />} />

          <Route path='/shipped'  element={<Shipped />} />

          <Route path='/newpassword'  element={<NewPass />} />

          <Route

            path="/admin_home"

            element={hasAccessToken ? <Admin /> : <Navigate to="/" />}

          />

          <Route path='*' element={<NotFound />} />

        </Routes>

    </div>

    </BrowserRouter>

  );
}




export default App;
