import a from '../Admin/Admin.module.scss'
import React, { CSSProperties } from 'react';
import Select from 'react-select';

import {
    ColourOption,
    colourOptions,
    FlavourOption,
    GroupedOption,
    groupedOptions,
  } from '../../';

  const groupStyles = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  };

  const groupBadgeStyles: CSSProperties = {
    backgroundColor: '#EBECF0',
    borderRadius: '2em',
    color: '#172B4D',
    display: 'inline-block',
    fontSize: 12,
    fontWeight: 'normal',
    lineHeight: '1',
    minWidth: 1,
    padding: '0.16666666666667em 0.5em',
    textAlign: 'center',
  };

  const formatGroupLabel = (data: GroupedOption) => (
    <div style={groupStyles}>
      <span>{data.label}</span>
      <span style={groupBadgeStyles}>{data.options.length}</span>
    </div>
  );

export default function Invitation () {

    return (

        <>

        <section className={a.section__home}>
            <div className="container">

                <p className={a.invitation__title}>Приглашение в админку</p>

                <form className={a.invitation}>

                    <input type="text" className={a.invitation__input} placeholder='Имя Фамилия'/>

                    <input type="text" className={a.invitation__input} placeholder='Почта'/>

                    <select className={a.invitation__select}>

                        <option value="admin">Админ</option>

                        <option value="advmoderator">Модератор рекламных кампаний</option>

                        <option value="sitemoderator">Модератор рекламных площадок</option>

                        <option value="lotmoderator">Модератор лотов аукциона</option>

                        <option value="supermanager">Суперменеджер</option>

                        <option value="manager">Менеджер</option>

                    </select>

                    <button className={a.invitation__btn}>
                        Добавить 
                    </button>



                </form>

            </div>
        </section>

        </>

    )

}
