import React from 'react';
import { useState } from 'react';
import s from './Statistics.module.scss'
import Select from 'react-select';
import { DatePicker } from 'antd';

import setting from './img/setting.svg'
import setbar from './img/setbar.svg'

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const labels = ['12.03.23', '12.03.30', '12.05.23', '12.06.23', '12.07.23', '12.08.23', '12.10.23'];

export const data = {
  labels,
  datasets: [
    {
      label: '',
      data: labels.map(() => Math.random() * 2000 - 1000),
      borderColor: '#FFA589',
      backgroundColor: '#FFA589',
    },
    {
      label: '',
      data: labels.map(() => Math.random() * 2000 - 1000),
      borderColor: '#FFA9F6',
      backgroundColor: '#FFA9F6',
    },

    {
      label: '',
      data: labels.map(() => Math.random() * 2000 - 1000),
      borderColor: '#BDE47E',
      backgroundColor: '#BDE47E',
    },

    {
      label: '',
      data: labels.map(() => Math.random() * 2000 - 1000),
      borderColor: '#6792FF',
      backgroundColor: '#6792FF',
    },

  ],
};

export default function Statisticss () {

  const optionsThree = [

        { value: 'option1', label: 'Опция 1' },
        { value: 'option2', label: 'Опция 2' },
        { value: 'option3', label: 'Опция 3' },

    ];

      const customStyles = {

        control: (provided) => ({
          ...provided,
          border: '1px solid var(--interface-seventh, #EBEDF5)',
          borderRadius: '8px',
          Color: '#1B1C1F',
        }),

        option: (provided, state) => ({

          ...provided,
          backgroundColor: state.isSelected ? '#007bff' : 'white',
          color: state.isSelected ? 'white' : 'black',
        }),

      };

      const [selectedOption, setSelectedOption] = useState(null); // Изначально выбранной опции нет

      const handleSelectChange = (selectedOption) => {

        setSelectedOption(selectedOption);

      };

      const [selectedDate, setSelectedDate] = useState(null);

      const handleDateChange = (date, dateString) => {
        setSelectedDate(date);
      };

      const DataStyle = {
        color: 'black',
        marginBottom: '0',
        height: '38px' ,
        border: '1px solid var(--interface-seventh, #EBEDF5)',
      };


      const optionsTwo = [

        { value: 'option1', label: 'Опция 1' },
        { value: 'option2', label: 'Опция 2' },
        { value: 'option3', label: 'Опция 3' },

    ];

    const customStylesTwo = {

      control: (provided) => ({
        ...provided,
        border: '1px solid var(--interface-seventh, #EBEDF5)',
        borderRadius: '8px',
        Color: '#1B1C1F',
        zIndex: -1,

      }),

      option: (provided, state) => ({

        ...provided,
        backgroundColor: state.isSelected ? '#007bff' : 'white',
        color: state.isSelected ? 'white' : 'black',
      }),

    };

    const [selectedOptionTwo, setSelectedOptionTwo] = useState(null); 

    const handleSelectChangeTwo = (selectedOptionTwo) => {

      setSelectedOptionTwo(selectedOptionTwo);

    };

    return (

        <>

        <section className={s.section__static} >

            <div className="container">

                <p className={s.static__title}>
                Моя статистика
                </p>

                <div className={s.static__header} >

                    <div className={s.static__header__one}>

                        <p className={s.static__header__one__title}>Неделя</p>

                        <p className={s.static__header__one__title}>Прошлая неделя</p>

                        <p className={s.static__header__one__title}>Последние 7 дней</p>

                        <p className={s.static__header__one__title}>Месяц</p>

                        <p className={s.static__header__one__title}>Квартал</p>

                        <p className={s.static__header__one__title}>Год</p>

                    </div>

                <Select
                    options={optionsThree}
                    value={selectedOption}
                    onChange={handleSelectChange}
                    className={s.static__header__select}
                    isClearable={true}
                    styles={customStyles}
                    placeholder="Детализация по дням" 
                />


                <DatePicker onChange={handleDateChange} style={DataStyle} placeholder='Выбрать даты'/>

                </div>

                <div className={s.static__center}>

                  <p className={s.static__center__title}>
                  Партнеры
                  </p>

                  <Select
                    options={optionsTwo}
                    value={selectedOptionTwo}
                    onChange={handleSelectChangeTwo}
                    className={s.static__center__select}
                    isClearable={true}
                    styles={customStylesTwo}
                    placeholder="Все" 
                />



                </div>

                <div className={s.static__footer}>

                  <p className={s.static__center__title}>
                  Тип бизнеса
                  </p>

                  <div className={s.static__footer__one}>

                     <p className={s.static__header__one__title}>Кросс-промо</p>

                     <p className={s.static__header__one__title}>CPC</p>

                     <p className={s.static__header__one__title}>Все</p>

                  </div>

                </div>

            </div>

        </section>

        <div className='section__staticTwo'>

          <div className="container">

            <div className=''></div>

            <div className='staticTwo__header'>

              <div className='static__header__info'>

                <p className='static__header__info__title'>Общий заработок (RUB):</p>

                <p className='static__header__info__sum'>21 593 047, 27</p>

              </div>

              {/* // setting right */}

              <div className='staticTwo__header__setting'>

                <img src={setting} alt="" />

                <img src={setbar} alt="" />

              </div>

              <Line data={data}  style={{ zIndex: -1 }} />

              <div className='staticTwo__header__footer'>

                <label className='staticTwo__header__footer__label'>

                  <input type="checkbox" />

                  <p>AlfaBank Promo 2</p>

                </label>

                <label className='staticTwo__header__footer__label'>

                  <input type="checkbox" />

                  <p>AlfaBank P1</p>

                </label>

                <label className='staticTwo__header__footer__label'>

                  <input type="checkbox" />

                  <p>Avito</p>

                </label>

                <label className='staticTwo__header__footer__label'>

                  <input type="checkbox" />

                  <p>Mvideo RT</p>

                </label>

              </div>

            </div>

          </div>

        </div>

        </>


    )

}
